import React, { Component } from "react";
import { PageHeader, ListGroup } from "react-bootstrap";

class Transactions extends Component {
    constructor(props) {
        super(props);

        this.state = {
          transactions: [],
          period: "last 30 days"
        };

        this.handleChange = this.handleChange.bind(this);
    }

    componentDidMount() {
        fetch('posts').then(res => res.json()).then(transactions => this.setState({transactions})) 
    }

    handleChange(event) {
        this.setState({period: event.target.value});
    }

    renderTransactionsList(transactions) {
        return null;
    }

    render() {
        return (
            <div className="transactions">
                <PageHeader>Your Transactions</PageHeader>
                <label>
                    Choose the period to display:
                    <select value={this.state.period}>
                        <option value="30d"> Last 30 days </option>
                        <option value="3m"> Last 3 months </option>
                        <option value="6m"> Last 6 months </option>
                    </select>
                </label> 
                <ListGroup>
                    {this.renderTransactionsList(this.state.tractions)}
                </ListGroup>
            </div>
        );
    }
}

export default Transactions;
